import React, { useState, useEffect } from "react";
import Image from "./image";

const MemoryCardGame = () => {
  const [image, setImage] = useState(Image);
  useEffect(() => {
    setImage(
      image.sort(() => {
        return Math.random() - 0.5;
      })
    );
  }, []);

  return (
    <div className="h-screen flex justify-center items-center">
      <div className="grid grid-cols-4 gap-6 items-center justify-center p-10 bg-purple-700">
        {image.map((el, id, pick) => {
          return (
            <div
              className={` w-[200px] h-[200px] rounded-2xl overflow-hidden transform-opacity duration-500`}
              onClick={() => {
                pick = !pick;
              }}
            >
              <img
                src={el.src}
                alt=""
                className={`absolute z-10 w-[200px] h-[200px]`}
              />
              <div className="absolute w-[200px] h-[200px] bg-blue-gray-50"></div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default MemoryCardGame;
