import React from "react";
import { useState, createContext, useEffect } from "react";
import axios from "axios";

const api = "Aoskv8a0fz4OfuG5I6cGOeFFlQJiGRuf";

export const Context = createContext({
  isDonationModalOpen: false,
  setIsDonationModalOpen: () => void 0,
});

export function Provider({ children }) {
  const [data, SetData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [inputvalue, setValue] = useState("");
  const instance = axios.create({
    baseURL: `https://api.giphy.com/v1/gifs/search?api_key=${api}&q=${inputvalue}&limit=25&offset=0&rating=g&lang=en`,
  });

  useEffect(() => {
    const resInterceptor = (res) => {
      console.log(res);
      setErrorMessage("");
      setLoading(false);
      return res.data.data;
    };
    const errInterceptor = (err) => {
      setErrorMessage(err.message);
      return Promise.reject(err);
    };
    const interceptor = axios.interceptors.response.use(
      resInterceptor,
      errInterceptor
    );
    return () => axios.interceptors.response.eject(interceptor);
  }, []);

  useEffect(() => {
    const getData = async () => {
      try {
        const res = await instance.get("/");
        SetData(res.data.data);
      } catch (error) {
        console.log(error);
      }
    };
    getData();
  }, [inputvalue]);
  return (
    <Context.Provider value={{ data, setValue, inputvalue }}>
      {children}
    </Context.Provider>
  );
}
