import React from "react";
import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router";

const PostId = () => {
  const { Id } = useParams();

  const instance = axios.create({
    baseURL: `https://dummyapi.io/data/v1/post/${Id}`,
    headers: { "app-id": "636e136b8af6961b47361447" },
  });

  const [data, setData] = useState();

  useEffect(() => {
    const fetchPost = async () => {
      try {
        const res = await instance.get("/");
        setData(res.data);
      } catch (err) {
        console.log(err.message);
      }
    };
    fetchPost();
  }, [Id]);
  console.log(data);
  return (
    <div className="pt-20 lg:px-[180px] xl:px-[270px]">
      <div className="">
        <div className="">{data?.text}</div>
        <div className="">
          <img src={data?.owner.picture} alt="" />
          <div className="">
            <p>{data?.owner.firstName}</p>
            <p>{data?.owner.lastName}</p>
          </div>
        </div>
      </div>
      <img src="" alt="" />
    </div>
  );
};

export default PostId;
