import React, { useState } from "react";
import { Data } from "./data";

const Index = () => {
  const [item, setItem] = useState(new Array(9).fill(true));
  const [num, setNum] = useState([]);
  const generate = () => {
    setItem(
      Data.sort(() => {
        return Math.random() - 0.5;
      })
    );
  };
  const start = () => {
    let newArr = item;

    newArr.map((el) => {
      console.log(el);
      el[1] = false;
    });
    setItem([...newArr]);
    setTimeout(() => {
      newArr.map((el) => {
        el[1] = true;
      });
      setItem([...newArr]);
    }, 3000);
  };

  const checkNum = (el) => {
    setNum((prev) => [...prev, el]);
    console.log(el);
  };

  const check = (index) => {};
  return (
    <div className="w-full h-screen flex flex-col gap-20 justify-center items-center">
      <div className="grid grid-cols-3 gap-10 justify-center items-center">
        {item.map((el, index) => {
          return (
            <div key={index}>
              {el[1] ? (
                <div
                  className="bg-lime-500 border-teal-500 w-40 h-40 flex items-center justify-center text-white text-2xl"
                  onClick={() => {
                    check(index);
                    checkNum(el[0]);
                  }}
                >
                  {el[0]}
                </div>
              ) : (
                <div
                  className="bg-lime-500 border-teal-500 w-40 h-40"
                  onClick={() => {
                    checkNum(el[0]);
                  }}
                ></div>
              )}
            </div>
          );
        })}
      </div>
      <button
        className="px-14 py-6 rounded-3xl bg-deep-orange-accent-400"
        onClick={() => {
          generate();
          start();
        }}
      >
        Start
      </button>
    </div>
  );
};

export default Index;
