import React, { useState } from "react";
import Image from "./image";
import "./Memory.css";
const MemoryCardGame = () => {
  const [ID, setID] = useState([]);
  const [image, setImage] = useState(
    Image.sort(() => {
      return Math.random() - 0.5;
    })
  );

  const flip = (index) => {
    const newArr = image;
    newArr[index].pick = !newArr[index].pick;
    setImage([...newArr]);
  };

  const flipBackTrue = (first, second) => {};
  const flipBackFalse = () => {
    image.map((el) => {
      el.pick = false;
    });
  };

  const checkId = (id, index) => {
    setID(ID.concat([[id, index]]));
    console.log(ID);
    if (ID.length === 2) {
      if (ID[0] === ID[1]) {
        flipBackTrue(ID[0], ID[1]);
      } else {
        flipBackFalse(ID[0], ID[1]);
      }
    }
  };

  return (
    <div className="h-screen flex justify-center items-center">
      <div className="grid grid-cols-4 gap-6 items-center justify-center p-10 bg-purple-700">
        {image.map((el, index) => {
          return (
            <div
              className="flip-card"
              key={index}
              onClick={() => {
                flip(index);
                checkId(el.id, index);
              }}
            >
              <div
                className="flip-card-inner"
                style={{
                  transform: `rotateY(${!el.pick ? "180deg" : "0deg"})`,
                }}
              >
                <div className="flip-card-front">
                  <img
                    src={el.src}
                    alt="Avatar"
                    style={{ width: "250px", height: "250px" }}
                  />
                </div>
                <div className="flip-card-back"></div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default MemoryCardGame;
