import React, { useState, useEffect } from "react";
import Image from "./image";

const MemoryCardGame = () => {
  const [click, setClick] = useState(false);
  const [image, setImage] = useState(Image);
  useEffect(() => {
    setImage(
      image.sort(() => {
        return Math.random() - 0.5;
      })
    );
  });

  return (
    <div className="h-screen flex justify-center items-center">
      <div className="grid grid-cols-4 gap-6 items-center justify-center p-10 bg-purple-700">
        {image.map((el, id, pick) => {
          return (
            <div
              className="w-[200px] h-[200px] rounded-2xl overflow-hidden transform duration-500"
              style={{
                transform: `rotateY(${click ? "180deg" : "-180deg"})`,
              }}
              onClick={() => setClick(!click)}
            >
              <img src="" alt="" />
              <img src={el.src} alt="" className="w-[200px] h-[200px]" />
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default MemoryCardGame;
