import React, { useState, useEffect } from "react";
import top from "../../IMG/UpperBack.png";
import bottom from "../../IMG/LowerBack.png";
import target from "../../IMG/Rectangle.png";
import { transform } from "framer-motion";

const GameIndex = () => {
  const [click, setClick] = useState(false);
  const [score, setScore] = useState(0);
  const [time, setTime] = useState();
  const [move, setMove] = useState([
    false,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
  ]);
  move[5] = true;
  console.log(move[5]);
  const translate = () => {
    let number = Math.floor(Math.random() * 15);
    setMove((move[number] = true));
    setTimeout(() => {
      move[number] = false;
    }, 500);
  };
  useEffect(() => {
    let interval = null;
    interval = setInterval(() => {
      translate();
    }, [800]);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="w-full h-screen bg-[#b40001] flex flex-col gap-14 items-center justify-center">
      <div className="pt-28 flex gap-40 text-white text-2xl">
        <div className="">Time:{time}</div>
        <div className="">Score:{score}</div>
      </div>
      <div className="grid grid-cols-5">
        {move.map((boolen, index) => (
          <div
            className="relative flex flex-col items-center justify-center pt-10"
            key={index}
          >
            <img src={top} alt="" className="w-0.4" />
            <img
              src={target}
              alt=""
              className={`${
                boolen ? "-translate-y-5" : "translate-y-5"
              } absolute w-0.2 transition-transform duration-100 ease-in`}
            />
            <img src={bottom} alt="" className="w-0.4 -translate-y-3" />
          </div>
        ))}
      </div>
      <div className="py-40">
        <button
          className="bg-teal-accent-400 px-24 py-7 rounded-full text-white cursor-pointer active:bg-teal-accent-700"
          onClick={() => {
            setClick(!click);
          }}
        >
          START
        </button>
      </div>
    </div>
  );
};

export default GameIndex;
