import React, { useState, useEffect } from "react";
import { Data } from "./data";

const Index = () => {
  const [item, setItem] = useState([]);
  const [num, setNum] = useState([]);
  const [visible, setVisible] = useState(false);
  const start = () => {
    generate();
    setVisible(true);
    setTimeout(() => {
      setVisible(false);
    }, 3000);
  };
  const generate = () => {
    setItem(Data.sort(() => 0.5 - Math.random()));
  };

  const checkNum = (el) => {
    setNum((prev) => [...prev, el]);
    console.log(num);
  };

  const check = (index) => {};
  return (
    <div className="w-full h-screen flex flex-col gap-20 justify-center items-center">
      <div className="grid grid-cols-3 gap-10 justify-center items-center">
        {item.map((el, index) => {
          return (
            <div key={index}>
              <div
                className="bg-lime-500 border-teal-500 w-40 h-40 flex items-center justify-center text-white text-2xl"
                onClick={() => {
                  check(index);
                }}
              >
                {visible && el[0]}
              </div>
            </div>
          );
        })}
      </div>
      <button
        className="px-14 py-6 rounded-3xl bg-deep-orange-accent-400"
        onClick={() => {
          start();
        }}
      >
        Start
      </button>
    </div>
  );
};

export default Index;
