// import Index from "./Pages/RouterChallange/Index";
import { motion, AnimatePresence } from "framer-motion";

const App = ({ isVisible }) => {
  <AnimatePresence>
    {isVisible && (
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
      />
    )}
  </AnimatePresence>;
};

export default App;
