import React, { useState } from "react";
import top from "../../IMG/UpperBack.png";
import bottom from "../../IMG/LowerBack.png";
import target from "../../IMG/Rectangle.png";

const GameIndex = () => {
  const [score, setScore] = useState(0);
  const [time, setTime] = useState();
  const [move, setMove] = useState([
    false,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
    false,
  ]);

  const Start = () => {};
  return (
    <div className="w-full h-screen bg-[#b80404] flex flex-col gap-14 items-center justify-center">
      <div className="flex gap-40 text-white text-2xl">
        <div className="">Time:{time}</div>
        <div className="">Score:{score}</div>
      </div>
      {move.map((boolen) => (
        <div className="relative flex flex-col items-center justify-center">
          <img src={top} alt="" className="z-10 w-0.4" />
          <img src={target} alt="" className="absolute top-4 z-20 w-0.2" />
          <img src={bottom} alt="" className="absolute top-[51px] z-30 w-0.4" />
        </div>
      ))}
      <div className="py-40">
        <button
          className="bg-teal-accent-400 px-24 py-7 rounded-full text-white cursor-pointer active:bg-teal-accent-700"
          onClick={() => {
            Start();
          }}
        >
          START
        </button>
      </div>
    </div>
  );
};

export default GameIndex;
