import React, { useState } from "react";
import { Data } from "./data";

const Index = () => {
  const [item, setItem] = useState(
    Data.sort(() => {
      return Math.random() - 0.5;
    })
  );

  const start = () => {
    let newArr = item;
    newArr.map((el) => {
      el[1] = !el[1];
    });
    setItem(newArr);
    console.log("asdhjaks");
  };

  return (
    <div className="w-full h-screen flex flex-col gap-20 justify-center items-center">
      <div className="grid grid-cols-3 gap-10 justify-center items-center">
        {item.map((el, index) => {
          return (
            <div key={index}>
              <div
                className={`${
                  el[1] ? "flex" : "hidden"
                } bg-lime-500 border-teal-500 w-40 h-40 items-center justify-center text-white text-2xl`}
              >
                {el[0]}
              </div>
              <div
                className={`${
                  el[1] ? "hidden" : "block"
                } bg-lime-500 border-teal-500 w-40 h-40`}
              ></div>
            </div>
          );
        })}
      </div>
      <button
        className="px-14 py-6 rounded-3xl bg-deep-orange-accent-400"
        onClick={() => {
          start();
        }}
      >
        Start
      </button>
    </div>
  );
};

export default Index;
