import React, { useState } from "react";
import { Data } from "./data";

const Index = () => {
  const [item, setItem] = useState(
    Data.sort(() => {
      return Math.random() - 0.5;
    })
  );
  return (
    <div className="w-full h-screen flex flex-col gap-20 justify-center items-center">
      <div className="grid grid-cols-3 gap-10 justify-center items-center">
        {item.map((el, index) => {
          return (
            <>
              <div className="bg-lime-500 border-teal-500 w-40 h-40 flex items-center justify-center text-white text-2xl">
                {el[0]}
              </div>
            </>
          );
        })}
      </div>

      <button className="px-14 py-6 rounded-3xl bg-deep-orange-accent-400">
        Start
      </button>
    </div>
  );
};

export default Index;
